from gb_department_orders as dor left join gb_distributer_goods as dg on dg.gb_distributer_goods_id = dor.gb_DO_dis_goods_id left join gb_distributer_standard as gds on gds.gb_DS_dis_goods_id = dg.gb_distributer_goods_id left join gb_distributer_goods_shelf_goods as gdgsg on gdgsg.gb_DGSG_dis_goods_id = dg.gb_distributer_goods_id left join gb_distributer_goods_shelf as gdgs on gdgs.gb_distributer_goods_shelf_id = gdgsg.gb_DGSG_shelf_id left join gb_department_dis_goods as gddg on gddg.gb_DDG_dis_goods_id = dg.gb_distributer_goods_id and gddg.gb_DDG_department_father_id = #{toDepId} and gddg.gb_department_dis_goods_id = gdgsg.gb_DGSG_dep_dis_goods_id left join gb_department_orders as newDor on newDor.gb_DO_dis_goods_id = dg.gb_distributer_goods_id and newDor.gb_DO_buy_status = 0 and newDor.gb_DO_to_department_id = #{toDepId} and newDor.gb_DO_department_id != #{toDepId} and newDor.gb_DO_weight_goods_id is null
left join gb_department as ds on ds.gb_department_id = newDor.gb_DO_department_id left join gb_department as df on df.gb_department_id = ds.gb_department_father_id left join gb_distributer_weight_goods as pre_gdwg on pre_gdwg.gb_dwg_dis_goods_id = dg.gb_distributer_goods_id and pre_gdwg.gb_dwg_status = -1 and pre_gdwg.gb_dwg_dep_id = #{toDepId} left join gb_department_orders as pre_dor on pre_dor.gb_DO_dis_goods_id = dg.gb_distributer_goods_id and pre_dor.gb_DO_weight_goods_id = pre_gdwg.gb_distributer_weight_goods_id left join gb_department as pre_ds on pre_ds.gb_department_id = pre_dor.gb_DO_department_id and pre_dor.gb_DO_department_id != #{toDepId} left join gb_department as pre_df on pre_df.gb_department_id = pre_ds.gb_department_father_id
left join gb_department_orders as selfDor on selfDor.gb_DO_dis_goods_id = dg.gb_distributer_goods_id and selfDor.gb_DO_buy_status < 3 and selfDor.gb_DO_department_id = #{toDepId}
and gdgsg.gb_distributer_goods_shelf_goods_id is not null
and dor.gb_DO_distributer_id = #{disId}
and dor.gb_DO_status < #{status}
and dor.gb_DO_buy_status < #{buyStatus}
and dor.gb_DO_arrive_date = #{arriveDate}
and dor.gb_DO_department_father_id = #{depFatherId}
and dor.gb_DO_goods_type = #{goodsType}
and dor.gb_DO_to_department_id = #{toDepId}
and dor.gb_DO_order_type = #{orderType}
and dg.gb_dg_is_self_control = #{isSelf}
and dor.gb_DO_weight_total_id = #{weightId}
and dor.gb_DO_weight_goods_id = #{weightGoodsId}
and dor.gb_DO_dis_goods_father_id = #{goodsFatherId}
order by gdgs.gb_distributer_goods_shelf_sort, gdgsg.gb_DGSG_sort, newDor.gb_DO_department_id,pre_dor.gb_DO_department_id

select COUNT(*)   from gb_distributer_goods as dg
left join gb_department_orders as dor on dor.gb_DO_dis_goods_id = dg.gb_distributer_goods_id

    and dor.gb_DO_distributer_id = #{disId}


    and dor.gb_DO_status < #{status}


    and dor.gb_DO_buy_status < #{buyStatus}


    and dor.gb_DO_arrive_date = #{arriveDate}


    and dor.gb_DO_department_father_id = #{depFatherId}


    and dor.gb_DO_goods_type = #{goodsType}


    and dor.gb_DO_to_department_id = #{toDepId}


    and dor.gb_DO_order_type = #{orderType}


    and dg.gb_dg_is_self_control = #{isSelf}


    and dor.gb_DO_weight_total_id = #{weightId}


    and dor.gb_DO_weight_goods_id = #{weightGoodsId}


    and dor.gb_DO_dis_goods_father_id = #{goodsFatherId}

select * from gb_department_orders as dor
left join gb_department as dep on dep.gb_department_id = dor.gb_DO_department_father_id


        and dor.gb_DO_distributer_id = #{disId}


        and dor.gb_DO_department_father_id = #{depFatherId}


        and dor.gb_DO_department_id = #{depId}


        and dor.gb_DO_status = #{equalStatus}


        and dor.gb_DO_status > #{equalStatus}


        and dor.gb_DO_status < #{status}



        and dor.gb_DO_arrive_date >=  #{startDate}



        and dor.gb_DO_arrive_date <=  #{stopDate}




        and dor.gb_DO_nx_distributer_goods_id = #{nxDisGoodsId}
