mapper.select:
select count(*) from gb_department_dis_goods


        and gb_DDG_department_father_id = #{depFatherId}


        and gb_DDG_dis_goods_father_id = #{fatherId}

select * from gb_department_dis_goods as gddg
left join gb_distributer_goods as gdg on gdg.gb_distributer_goods_id = gddg.gb_DDG_dis_goods_id
left join gb_department_goods_stock as gdgs on gdgs.gb_dgs_gb_dep_dis_goods_id = gddg.gb_department_dis_goods_id
left join gb_department as gd on gd.gb_department_id = gddg.gb_DDG_department_id


        and gb_DDG_department_id = #{depId}


        and gb_DDG_dis_goods_id = #{disGoodsId}


        and gb_DDG_department_father_id = #{depFatherId}


        and gb_DDG_dis_goods_father_id = #{disGoodsFatherId}


        and gb_DDG_nx_distributer_goods_id = #{nxGoodsId}


        and gb_DDG_dis_goods_father_id = #{disGoodsFatherId}


        and gdg.gb_dg_goods_type = #{type}


        and gdg.gb_dg_goods_inventory_type = #{inventoryType}


        and gdgs.gb_DGS_status > #{dayuStatus}


        and gdgs.gb_DGS_rest_weight > 0


        and gd.gb_department_type = #{depType}


        and gdg.gb_dg_is_self_control = #{isSelf}


        and gdg.gb_dg_control_fresh = #{fresh}

select * , greatGrand.gb_distributer_father_goods_id 'greatGrand_gb_distributer_father_goods_id', greatGrand.gb_dfg_fathers_father_id 'greatGrand_gb_dfg_fathers_father_id', greatGrand.gb_dfg_father_goods_name 'greatGrand_gb_dfg_father_goods_name', greatGrand.gb_dfg_father_goods_img 'greatGrand_gb_dfg_father_goods_img', greatGrand.gb_dfg_father_goods_sort 'greatGrand_gb_dfg_father_goods_sort',
great.gb_distributer_father_goods_id 'g_gb_distributer_father_goods_id', great.gb_dfg_fathers_father_id 'g_gb_dfg_fathers_father_id', great.gb_dfg_father_goods_name 'g_gb_dfg_father_goods_name', great.gb_dfg_father_goods_img 'g_gb_dfg_father_goods_img', great.gb_dfg_father_goods_sort 'g_gb_dfg_father_goods_sort', dfg.gb_distributer_father_goods_id 'f_gb_distributer_father_goods_id', dfg.gb_dfg_fathers_father_id 'f_gb_dfg_fathers_father_id', dfg.gb_dfg_father_goods_name 'f_gb_dfg_father_goods_name', dfg.gb_dfg_father_goods_img 'f_gb_dfg_father_goods_img', dfg.gb_dfg_father_goods_sort 'f_gb_dfg_father_goods_sort', inventoryStock.gb_department_goods_stock_id 'inventory_gb_department_goods_stock_id', inventoryStock.gb_dgs_gb_dep_dis_goods_id 'inventory_gb_dgs_gb_dep_dis_goods_id', inventoryStock.gb_dgs_weight 'inventory_gb_dgs_weight', inventoryStock.gb_dgs_date 'inventory_gb_dgs_date', inventoryStock.gb_dgs_week 'inventory_gb_dgs_week'
from gb_department_dis_goods as ddg left join gb_department_goods_stock as gdgs on gdgs.gb_dgs_gb_dep_dis_goods_id = ddg.gb_department_dis_goods_id and gdgs.gb_dgs_rest_weight > 0 and gdgs.gb_dgs_status > -1 left join gb_distributer_goods as dg on dg.gb_distributer_goods_id = ddg.gb_DDG_dis_goods_id left join gb_department as gd on gd.gb_department_id = dg.gb_dg_gb_department_id left join gb_distributer_alias as da on da.gb_DA_dis_goods_id = ddg.gb_DDG_dis_goods_id left join gb_distributer_standard as ds on ds.gb_DS_dis_goods_id = dg.gb_distributer_goods_id left join gb_distributer_father_goods as dfg on dfg.gb_distributer_father_goods_id = ddg.gb_DDG_dis_goods_father_id left join gb_distributer_father_goods as great on great.gb_distributer_father_goods_id = dfg.gb_dfg_fathers_father_id left join gb_distributer_father_goods as greatGrand on greatGrand.gb_distributer_father_goods_id = great.gb_dfg_fathers_father_id left join gb_department_orders as dor on dor.gb_DO_dis_goods_id = ddg.gb_DDG_dis_goods_id and dor.gb_DO_status < #{status} and dor.gb_DO_status > -1 and dor.gb_DO_department_id = #{depId} left join nx_distributer as nd on nd.nx_distributer_id = dg.gb_dg_nx_distributer_id left join gb_department_goods_stock as inventoryStock on inventoryStock.gb_dgs_gb_dep_dis_goods_id = ddg.gb_department_dis_goods_id left join gb_department_goods_stock_reduce as gdgsr on gdgsr.gb_dgsr_gb_goods_stock_id = gdgs.gb_department_goods_stock_id and gdgsr.gb_dgsr_type = 4 and gdgsr.gb_dgsr_status = -1 left join gb_department_goods_daily as gsrd on gsrd.gb_dgd_gb_dep_dis_goods_id = ddg.gb_department_dis_goods_id and gsrd.gb_dgd_date = #{date}
and ddg.gb_DDG_department_id = #{depId}
and dg.gb_dg_pull_off = #{pull}
and dg.gb_dg_dfg_goods_father_id is not null
and dg.gb_dg_dfg_goods_father_id is null
and dg.gb_dg_is_self_control = #{isSelf}
and greatGrand.gb_distributer_father_goods_id = #{greatGrandId}
order by greatGrand.gb_dfg_father_goods_sort, great.gb_dfg_father_goods_sort, dfg.gb_dfg_father_goods_sort, great.gb_dfg_nx_goods_id, dg.gb_dg_goods_py, gdgs.gb_dgs_full_time

select * , gdf.gb_department_id 'f_gb_department_id', gdf.gb_department_attr_name 'f_gb_department_attr_name', gdf.gb_department_name 'f_gb_department_name' from gb_department_dis_goods as ddg left join gb_distributer_goods as dg on dg.gb_distributer_goods_id = ddg.gb_DDG_dis_goods_id left join gb_distributer_alias as da on da.gb_DA_dis_goods_id = ddg.gb_DDG_dis_goods_id left join gb_distributer_standard as ds on ds.gb_DS_dis_goods_id = dg.gb_distributer_goods_id left join gb_distributer_father_goods as dfg on dfg.gb_distributer_father_goods_id = ddg.gb_DDG_dis_goods_father_id left join gb_department_orders as dor on dor.gb_DO_dis_goods_id = ddg.gb_DDG_dis_goods_id and dor.gb_DO_status < #{status} and dor.gb_DO_dep_dis_goods_id = ddg.gb_department_dis_goods_id left join gb_department as gd on gd.gb_department_id = ddg.gb_DDG_gb_department_id left join gb_department as gdf on gdf.gb_department_id = dor.gb_DO_department_father_id left join nx_distributer as nd on nd.nx_distributer_id = ddg.gb_DDG_nx_distributer_goods_id left join nx_distributer_goods as ndg on ndg.nx_distributer_goods_id = ddg.gb_DDG_nx_distributer_goods_id
and ddg.gb_DDG_department_father_id = #{depFatherId}
and ddg.gb_DDG_dis_goods_father_id = #{fatherId}
order by dg.gb_dg_dfg_goods_father_id, dg.gb_dg_goods_pinyin
limit #{offset}, #{limit}

select * from gb_department_dis_goods as ddg left join gb_department_goods_stock as gdgs on gdgs.gb_dgs_gb_dep_dis_goods_id = ddg.gb_department_dis_goods_id and gdgs.gb_dgs_rest_weight > 0 and gdgs.gb_dgs_status > -1 left join gb_distributer_goods as dg on dg.gb_distributer_goods_id = ddg.gb_DDG_dis_goods_id left join gb_distributer_standard as gds on gds.gb_DS_dis_goods_id = dg.gb_distributer_goods_id left join gb_distributer_alias as da on da.gb_DA_dis_goods_id = dg.gb_distributer_goods_id left join gb_department_orders as dor on dor.gb_DO_dis_goods_id = dg.gb_distributer_goods_id and dor.gb_DO_status < 2 and dor.gb_DO_status > -1 and dor.gb_DO_department_id = #{depId} left join gb_department_user as du on du.gb_department_user_id = dor.gb_DO_order_user_id left join nx_distributer as nd on nd.nx_distributer_id = dg.gb_dg_nx_distributer_id left join gb_department as gd on gd.gb_department_id = ddg.gb_DDG_gb_department_id
and ddg.gb_DDG_department_id = #{depId}
and ( gb_DDG_dep_goods_name like concat('%', #{searchStr}, '%') or gb_DDG_dep_goods_name like concat('%', #{searchStr}, '%') or da.gb_DA_alias_name like concat('%', #{searchStr}, '%') or gb_DDG_dep_goods_pinyin like concat('%', #{searchPinyin}, '%') or gb_DDG_dep_goods_py like concat('%', #{searchPinyin}, '%') or da.gb_DA_alias_name like concat('%', #{searchPinyin}, '%')
)
and ( gb_DDG_dep_goods_pinyin like concat('%', #{searchPinyin}, '%') or gb_DDG_dep_goods_py like concat('%', #{searchPinyin}, '%') or da.gb_DA_alias_name like concat('%', #{searchPinyin}, '%'))
and dg.gb_dg_is_self_control = #{isSelf}
order by dg.gb_DG_goods_sort

