mapper.select:
select *
from nx_distributer_goods
where nx_distributer_goods_id = #{value}

select count(*)
from nx_distributer_goods as ndg
left join nx_distributer_father_goods as ndfg on ndfg.nx_distributer_father_goods_id =
ndg.nx_dg_dfg_goods_grand_id


        and nx_dg_distributer_id = #{disId}


        and ndg.nx_dg_goods_is_hidden = #{isHidden}


        and nx_dg_purchase_auto = #{autoType}


        and ndg.nx_dg_supplier_id = #{supplierId}


        and ndg.nx_dg_dfg_goods_grand_id = #{grandId}


        and ndfg.nx_dfg_fathers_father_id = #{greatGrandId}

select *
from nx_distributer_goods as ndg
    left join nx_distributer_standard as nds on nds.nx_DS_dis_goods_id = ndg.nx_distributer_goods_id
         left join nx_distributer_father_goods as ndfg
                   on ndfg.nx_distributer_father_goods_id = ndg.nx_dg_dfg_goods_grand_id
         left join nx_department_dis_goods as nddg
                   on nddg.nx_DDG_dis_goods_id = ndg.nx_distributer_goods_id
                       and nddg.nx_DDG_department_id = #{nxDepId}
         left join nx_department_orders as ndo on ndo.nx_DO_dis_goods_id = ndg.nx_distributer_goods_id
    and ndo.nx_DO_status < 3 and ndo.nx_DO_department_id = #{nxDepId}
where ndfg.nx_dfg_fathers_father_id = #{grandId}  and ndg.nx_dg_goods_is_hidden = #{isHidden}
order by ndfg.nx_dfg_father_goods_sort, ndg.nx_dg_goods_sort, ndg.nx_dg_goods_sons_sort, ndg.nx_distributer_goods_id
limit #{offset}, #{limit}

select *
from nx_distributer_goods
where nx_dg_nx_goods_id = #{nxSGoodsId};

select * from nx_distributer_goods as ndg
left join nx_distributer_standard as nds on nds.nx_DS_dis_goods_id = ndg.nx_distributer_goods_id
left join nx_distributer_alias as nda on nda.nx_DA_dis_goods_id = ndg.nx_distributer_goods_id


        and nx_dg_distributer_id = #{disId}


        and nx_dg_goods_pinyin = #{searchPinyin}


        and nx_dg_nx_goods_id is not null

select * from nx_distributer_goods as ndg
left join nx_department_orders as ndo on ndo.nx_DO_department_id = #{depId}
and ndg.nx_distributer_goods_id = ndo.nx_DO_dis_goods_id and ndo.nx_DO_status < 3
left join nx_department_dis_goods as nddg on nddg.nx_DDG_dis_goods_id = ndg.nx_distributer_goods_id
and nddg.nx_DDG_department_id = #{depId}
left join nx_distributer_standard as nds on nds.nx_DS_dis_goods_id = ndg.nx_distributer_goods_id
left join nx_distributer_alias as nda on nda.nx_DA_dis_goods_id = ndg.nx_distributer_goods_id



        and nx_dg_distributer_id = #{disId}


        and nx_dg_goods_name = #{searchStr}


        and nx_dg_goods_standardname = #{standard}


        and nx_dg_nx_goods_id is not null

select * from nx_distributer_goods


        and nx_dg_distributer_id = #{disId}


        and nx_dg_goods_name = #{goodsName}


        and nx_dg_goods_brand = #{goodsBrand}


        and nx_dg_goods_detail = #{goodsDetail}


        and nx_dg_goods_standardname = #{goodsStandard}


        and nx_dg_goods_standard_weight = #{standardWeight}


        and nx_dg_goods_place = #{place}



        and nx_dg_dfg_goods_father_id = #{fatherId}

select *, ds.nx_department_id 'nx_department_id', ds.nx_department_name 'nx_department_name', ds.nx_department_attr_name 'nx_department_attr_name', ds.nx_department_type 'nx_department_type', ds.nx_department_father_id 'nx_department_father_id', ds.nx_department_is_group_dep 'nx_department_is_group_dep', df.nx_department_id 'dd_nx_department_id', df.nx_department_name 'dd_nx_department_name', df.nx_department_attr_name 'dd_nx_department_attr_name',
gds.gb_department_name 'gb_department_name', gds.gb_department_attr_name 'gb_department_attr_name', gds.gb_department_type 'gb_department_type', gds.gb_department_father_id 'gb_department_father_id', gds.gb_department_is_group_dep 'gb_department_is_group_dep', gdf.gb_department_id 'dd_gb_department_id', gdf.gb_department_name 'dd_gb_department_name', gdf.gb_department_attr_name 'dd_gb_department_attr_name'
from nx_distributer_goods as ndg left join nx_department_orders as ndo on ndo.nx_DO_dis_goods_id = ndg.nx_distributer_goods_id left join nx_department as ds on ds.nx_department_id = ndo.nx_DO_department_id left join nx_department df on ds.nx_department_father_id = df.nx_department_id left join gb_department as gds on gds.gb_department_id = ndo.nx_DO_gb_department_id left join gb_department as gdf on gdf.gb_department_id = gds.gb_department_father_id where nx_dg_nx_goods_id is null and nx_dg_distributer_id = #{disId}

select * from nx_distributer_goods as ndg


        and nx_dg_supplier_id = #{supplierId}


        and nx_dg_nx_goods_id = #{nxGoodsId}


        and nx_dg_nx_father_id = #{nxFatherId}


        and nx_dg_nx_great_grand_id = #{nxGreatGrandFatherId}


        and ndg.nx_dg_distributer_id = #{disId}


        and ndg.nx_dg_nx_goods_id = #{goodsId}


        and ndg.nx_dg_dfg_goods_father_id = #{dgFatherId}


        and ndg.nx_dg_dfg_goods_grand_id = #{grandId}


        and ndg.nx_dg_goods_is_hidden = #{isHidden}


        and nx_dg_purchase_auto = #{autoType}



order by ndg.nx_dg_goods_sort, ndg.nx_dg_goods_sons_sort

    limit #{offset}, #{limit}

select * from nx_distributer_goods


        and nx_dg_nx_father_id = #{nxFatherId}


        and nx_dg_nx_goods_id = #{nxGoodsId}


        and nx_dg_distributer_id = #{disId}


        and nx_dg_goods_name = #{goodsName}


        and nx_dg_goods_pinyin = #{searchPinyin}

select * from nx_distributer_goods as ndg
left join nx_distributer_father_goods as ndfg on
    ndfg.nx_distributer_father_goods_id = ndg.nx_dg_dfg_goods_grand_id


        and nx_dg_nx_goods_id = #{nxGoodsId}


        and ndg.nx_dg_supplier_id = #{supplierId}


        and nx_dg_purchase_auto = #{autoType}


        and ndg.nx_dg_goods_is_hidden = #{isHidden}

select * from nx_distributer_goods as ndg
 left join nx_goods as ng  on ng.nx_goods_id = ndg.nx_dg_nx_father_id


        and nx_dg_nx_goods_id = #{nxGoodsId}


        and ndg.nx_dg_supplier_id = #{supplierId}


        and nx_dg_purchase_auto = #{autoType}

select *
from  nx_distributer_goods as ndg
         left join gb_distributer_goods as gdg on gdg.gb_dg_nx_distributer_goods_id = ndg.nx_distributer_goods_id  and gdg.gb_dg_distributer_id = #{gbDisId}
         left join nx_distributer_father_goods as ndfg on ndfg.nx_distributer_father_goods_id = ndg.nx_dg_dfg_goods_grand_id


      and  ndfg.nx_dfg_fathers_father_id = #{grandId}


        and  ndfg.nx_distributer_father_goods_id = #{fatherId}

    and ndg.nx_dg_goods_is_hidden = 0


order by ndfg.nx_dfg_father_goods_sort, ndg.nx_dg_goods_sort, ndg.nx_distributer_goods_id

    limit #{offset}, #{limit}

select * from nx_distributer_goods as ndg
left join nx_distributer_standard as nds on nds.nx_DS_dis_goods_id = ndg.nx_distributer_goods_id
left join nx_distributer_alias as nda on nda.nx_DA_dis_goods_id = ndg.nx_distributer_goods_id


        and nx_dg_distributer_id = #{disId}


        and nda.nx_DA_alias_name = #{alias}


        and nda.nx_DA_dis_goods_id = #{disGoodsId}


        and nx_dg_goods_standardname = #{standard}
